package utils

import (
	"github.com/ethereum/go-ethereum/common"
	"github.com/stretchr/testify/assert"
	"math/big"
	"testing"
)

func TestApplyL1ToL2Alias(t *testing.T) {
	l1ContractAddress := common.HexToAddress("0x702942B8205E5dEdCD3374E5f4419843adA76Eeb")
	l2ContractAddress := ApplyL1ToL2Alias(l1ContractAddress)
	expected := common.HexToAddress("0x813A42B8205E5DedCd3374e5f4419843ADa77FFC")
	assert.Equal(t, expected, l2ContractAddress, "Addresses should be the same")
}

func TestUndoL1ToL2Alias(t *testing.T) {
	l2ContractAddress := common.HexToAddress("0x813A42B8205E5DedCd3374e5f4419843ADa77FFC")
	l1ContractAddress := UndoL1ToL2Alias(l2ContractAddress)
	expected := common.HexToAddress("0x702942B8205E5dEdCD3374E5f4419843adA76Eeb")
	assert.Equal(t, expected, l1ContractAddress, "Addresses should be the same")
}

func TestUndoL1ToL2AliasOffsetGreaterThanAddress(t *testing.T) {
	l1ContractAddress := UndoL1ToL2Alias(common.HexToAddress("0x100"))
	expected := common.HexToAddress("0xeeeeffffffffffffffffffffffffffffffffefef")
	assert.Equal(t, expected, l1ContractAddress, "Addresses should be the same")
}

func TestCreateAddress(t *testing.T) {
	address, err := CreateAddress(common.HexToAddress("0x36615Cf349d7F6344891B1e7CA7C72883F5dc049"), big.NewInt(1))
	assert.NoError(t, err, "CreateAddress should not return error")

	expected := common.HexToAddress("0x4B5DF730c2e6b28E17013A1485E5d9BC41Efe021")
	assert.Equal(t, expected, address, "Addresses should be the same")
}

func TestCreate2Address(t *testing.T) {
	address, err := Create2Address(
		common.HexToAddress("0x36615Cf349d7F6344891B1e7CA7C72883F5dc049"),
		common.Hex2Bytes("010001cb6a6e8d5f6829522f19fa9568660e0a9cd53b2e8be4deb0a679452e41"),
		common.Hex2Bytes("01"),
		common.LeftPadBytes(common.Hex2Bytes("01"), 32),
	)
	assert.NoError(t, err, "Create2Address should not return error")

	expected := common.HexToAddress("0x00223F7Acb90872d421aa70Da3505fAb9eAAc664")
	assert.Equal(t, expected, address, "Addresses should be the same")
}

func TestHashBytecode(t *testing.T) {
	bytecode := common.Hex2Bytes("")
	hashedBytecode, err := HashBytecode(bytecode)
	assert.NoError(t, err, "HashBytecode should not return error")

	expected := []byte{
		1, 0, 0, 27, 57, 231, 154, 55, 0, 164, 201, 96, 244, 120, 23, 112, 54,
		34, 224, 133, 160, 122, 88, 164, 112, 80, 0, 134, 48, 138, 74, 16,
	}
	assert.Equal(t, expected, hashedBytecode, "Hashes should be the same")
}

func TestHashBytecodeDoPadding(t *testing.T) {
	bytecode := common.Hex2Bytes("")
	hashedBytecode, err := HashBytecode(bytecode)
	assert.NoError(t, err, "HashBytecode should not return error")

	expected := []byte{
		1, 0, 1, 203, 106, 110, 141, 95, 104, 41, 82, 47, 25, 250, 149, 104,
		102, 14, 10, 156, 213, 59, 46, 139, 228, 222, 176, 166, 121, 69, 46,
		65,
	}
	assert.Equal(t, expected, hashedBytecode, "Hashes should be the same")
}

func TestHashBytecodeNotDivisibleBy32(t *testing.T) {
	bytecode := common.Hex2Bytes("0002")
	_, err := HashBytecode(bytecode)

	assert.Error(t, err, "Should throw error when bytecode is not dividable by 32")
	assert.EqualError(t, err, "bytecode length in bytes must be divisible by 32")
}
